mapper.select:
SELECT * FROM ( SELECT bw.bw_no, m.m_no, m.m_id,m.m_nick, bt.bt_name, bt.bt_no, s.s_content , bw.bw_title, bw.bw_hit, bw.bw_content, bw.bw_written_date, bw.bw_updated_date, bw.bw_report_num, bw.bw_island, bw.bw_recommend_num, (select count(*) from cm_comment where cm_type = '게시판' and cm_no2 = bw.bw_no) cm, ROW_NUMBER() OVER(ORDER BY bw.bw_no ASC) AS RNUM, ROW_NUMBER() OVER(ORDER BY bw.bw_no DESC) AS RNUM_COUNT FROM board_write bw INNER JOIN subject s ON bw.s_no=s.s_no INNER JOIN board_type bt ON bw.bt_no = bt.bt_no INNER JOIN member m ON bw.m_no=m.m_no
WHERE bt.bt_no BETWEEN 1 and 6
WHERE bt.bt_no = #{bt_no}
AND bw.bw_island = 0
) WHERE RNUM_COUNT BETWEEN #{scri.rowStart} AND #{scri.rowEnd} ORDER BY bw_no DESC

select bw_no, bt_no, s_no, bw_title, bw_content, bw_island
from board_write
where bw_no = #{bw_no}

mapper.update:
{ CALL
	DECLARE

	      TYPE bg_no_table IS TABLE OF trade_game.tg_no%TYPE 
	            INDEX BY BINARY_INTEGER;             
	      TYPE tg_name_table IS TABLE OF trade_game.tg_name%TYPE 
	            INDEX BY BINARY_INTEGER;       


		    tg_no_tab       tg_no_table;       	
		    tg_name_tab    tg_name_table;		
		    tg_price_tab    tg_price_table;      

		     nocnt binary_integer :=0;
		     gcnt binary_integer :=0;
		     j binary_integer :=0;


		     v_t_no     trade_game.t_no%type := #{tradeVO.t_no};


	   BEGIN


		  UPDATE 
		  	BOARD_WRITE
		  SET 
			BT_NO = #{board_writeVO.bt_no},
			S_NO = #{board_writeVO.s_no},
			BW_TITLE = #{board_writeVO.bw_title},
			BW_CONTENT = #{board_writeVO.bw_content},
			BW_UPDATED_DATE = SYSDATE


					, bw_island = 2

		  WHERE 
		  	BW_NO = #{board_writeVO.bw_no};

		  commit;




	     for tg_nolist in (select tg_no from trade_game where t_no = v_t_no) loop			     
	        nocnt := nocnt +1;			        
	        tg_no_tab(nocnt) := tg_nolist.tg_no;			        
	     end loop; 



			    gcnt := gcnt +1;
			    tg_name_tab(gcnt) := #{game};
			    tg_price_tab(gcnt)  := #{price};



	        IF tg_name_tab.count = tg_no_tab.count THEN

	           for cnt in 1..tg_no_tab.count  loop     
	               -- j := j +1;             
	                update trade_game SET tg_name = tg_name_tab(cnt), tg_price = tg_price_tab(cnt) where tg_no = tg_no_tab(cnt);
	                commit;        
	           end loop;

	        ELSIF tg_name_tab.count > tg_no_tab.count THEN

	           for cnt in 1..tg_no_tab.count  loop     
	                -- j := j +1;             
	                update trade_game SET tg_name = tg_name_tab(cnt), tg_price = tg_price_tab(cnt) where tg_no = tg_no_tab(cnt);
	                commit;        
	           end loop;

	           for cnt in tg_no_tab.count+1..tg_name_tab.count  loop     
	                -- j := j +1;             
	                insert into trade_game (tg_no, t_no, g_no, tg_name, tg_price) values (get_trade_game_seq, v_t_no, null,  tg_name_tab(cnt), tg_price_tab(cnt));
	                commit;        
	           end loop;

	        ELSE

	           for cnt in 1..tg_name_tab.count  loop     
	                -- j := j +1;             
	                update trade_game SET tg_name = tg_name_tab(cnt), tg_price = tg_price_tab(cnt) where tg_no = tg_no_tab(cnt);
	                commit;        
	           end loop;

	           for cnt in tg_name_tab.count+1..tg_no_tab.count  loop     
	                -- j := j +1;                    
	                DELETE 	FROM trade_game WHERE tg_no = tg_no_tab(cnt);
	                commit;        
	           end loop;

	        END IF;

	   END		 
}

mapper.select:

SELECT * FROM


			(select c.*, ROW_NUMBER() OVER(ORDER BY c.c_no DESC) AS RNUM from cafe c)


			(select c.*, ROW_NUMBER() OVER(ORDER BY c.c_no DESC) AS RNUM 
			from cafe c where substr(c_add,1,3) like #{c_add}||'%')


	WHERE RNUM BETWEEN #{scri.rowStart} AND #{scri.rowEnd}

mapper.update:
update cafe_review set cr_title = #{cafe_reivewVO.cr_title}, cr_content = #{cafe_reivewVO.cr_content}, cr_updated_date = SYSDATE
, cr_island = 2
where cr_no = #{cafe_reivewVO.cr_no}
