mapper.select:
select bw_no, bt_no, s_no, bw_title, bw_content
from board_write
where bw_no = #{bw_no}

mapper.update:
update board_write 
set bt_no = #{board_writeVO.bt_no}, 
s_no = #{board_writeVO.s_no}, 
bw_title = #{board_writeVO.bw_title}, 
bw_content = #{board_writeVO.bw_content} 
where bw_no = #{board_writeVO.bw_no}
